import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  Image,
  Dimensions,
  Alert,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { socketService } from '../services/socketService';

const { width } = Dimensions.get('window');
const POSTER_WIDTH = 200;
const POSTER_HEIGHT = 300;

export default function MagicianScreen() {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    setupSocketConnection();

    return () => {
      socketService.disconnect();
    };
  }, []);

  const setupSocketConnection = () => {
    socketService.connect();

    socketService.onConnect(() => {
      console.log('Magician connected to customer app');
      setIsConnected(true);
    });

    socketService.onDisconnect(() => {
      console.log('Magician disconnected from customer app');
      setIsConnected(false);
    });

    socketService.onMovieSelected((movie) => {
      console.log('Customer selected movie:', movie);
      setSelectedMovie(movie);

      // Show alert for new selection
      Alert.alert(
        'üé¨ New Movie Selection!',
        `Customer selected: ${movie.title}`,
        [{ text: 'OK' }]
      );
    });
  };

  const renderMovieSelection = () => {
    if (!selectedMovie) {
      return (
        <View style={styles.noSelectionContainer}>
          <Text style={styles.noSelectionText}>üé≠</Text>
          <Text style={styles.noSelectionTitle}>Waiting for Customer Selection</Text>
          <Text style={styles.noSelectionSubtitle}>
            When a customer selects a movie, it will appear here instantly!
          </Text>
        </View>
      );
    }

    return (
      <View style={styles.movieContainer}>
        <Text style={styles.selectionTitle}>üé¨ Customer's Movie Selection</Text>

        <View style={styles.movieCard}>
          <Image
            source={{ uri: `https://image.tmdb.org/t/p/w500${selectedMovie.poster_path}` }}
            style={styles.moviePoster}
            resizeMode="cover"
          />

          <View style={styles.movieInfo}>
            <Text style={styles.movieTitle}>{selectedMovie.title}</Text>
            <Text style={styles.movieYear}>
              {new Date(selectedMovie.release_date).getFullYear()}
            </Text>
            <Text style={styles.movieRating}>‚≠ê {selectedMovie.vote_average}/10</Text>
            <Text style={styles.movieOverview} numberOfLines={4}>
              {selectedMovie.overview}
            </Text>
          </View>
        </View>

        <View style={styles.selectionTime}>
          <Text style={styles.timeText}>
            Selected at: {new Date().toLocaleTimeString()}
          </Text>
        </View>
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>üé© Magician View</Text>
        <View style={styles.connectionStatus}>
          <View style={[styles.statusDot, { backgroundColor: isConnected ? '#4CAF50' : '#F44336' }]} />
          <Text style={styles.statusText}>
            {isConnected ? 'Connected' : 'Disconnected'}
          </Text>
        </View>
      </View>

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {renderMovieSelection()}
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1a1a1a',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingVertical: 15,
    backgroundColor: '#2d2d2d',
    borderBottomWidth: 1,
    borderBottomColor: '#404040',
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#ffffff',
  },
  connectionStatus: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  statusDot: {
    width: 8,
    height: 8,
    borderRadius: 4,
    marginRight: 8,
  },
  statusText: {
    fontSize: 14,
    color: '#ffffff',
  },
  content: {
    flex: 1,
    padding: 20,
  },
  noSelectionContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingVertical: 60,
  },
  noSelectionText: {
    fontSize: 80,
    marginBottom: 20,
  },
  noSelectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#ffffff',
    textAlign: 'center',
    marginBottom: 10,
  },
  noSelectionSubtitle: {
    fontSize: 16,
    color: '#cccccc',
    textAlign: 'center',
    lineHeight: 24,
  },
  movieContainer: {
    alignItems: 'center',
  },
  selectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 20,
    textAlign: 'center',
  },
  movieCard: {
    backgroundColor: '#2d2d2d',
    borderRadius: 15,
    padding: 20,
    width: '100%',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.3,
    shadowRadius: 8,
    elevation: 8,
  },
  moviePoster: {
    width: POSTER_WIDTH,
    height: POSTER_HEIGHT,
    borderRadius: 10,
    marginBottom: 20,
  },
  movieInfo: {
    alignItems: 'center',
    width: '100%',
  },
  movieTitle: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#ffffff',
    textAlign: 'center',
    marginBottom: 8,
  },
  movieYear: {
    fontSize: 16,
    color: '#cccccc',
    marginBottom: 8,
  },
  movieRating: {
    fontSize: 16,
    color: '#FFD700',
    marginBottom: 12,
  },
  movieOverview: {
    fontSize: 14,
    color: '#cccccc',
    textAlign: 'center',
    lineHeight: 20,
  },
  selectionTime: {
    marginTop: 20,
    padding: 15,
    backgroundColor: '#404040',
    borderRadius: 10,
  },
  timeText: {
    fontSize: 14,
    color: '#ffffff',
    textAlign: 'center',
  },
});
