import React, { useState, useEffect } from 'react';
import { StatusBar } from 'expo-status-bar';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { Platform } from 'react-native';

import HomeScreen from './src/screens/HomeScreen';
import GenreScreen from './src/screens/GenreScreen';
import MovieDetailScreen from './src/screens/MovieDetailScreen';
import MagicianScreen from './src/screens/MagicianScreen';

export default function App() {
  const [currentScreen, setCurrentScreen] = useState('Home');
  const [screenParams, setScreenParams] = useState({});

  useEffect(() => {
    // Handle URL routing for web
    if (Platform.OS === 'web') {
      const handleRouteChange = () => {
        const path = window.location.pathname;
        console.log('Current path:', path);

        if (path === '/Magician') {
          setCurrentScreen('Magician');
        } else if (path === '/Genre') {
          setCurrentScreen('Genre');
          // Extract genre from URL params if needed
        } else if (path === '/MovieDetail') {
          setCurrentScreen('MovieDetail');
          // Extract movie ID from URL params if needed
        } else {
          setCurrentScreen('Home');
        }
      };

      // Set initial route
      handleRouteChange();

      // Listen for URL changes
      window.addEventListener('popstate', handleRouteChange);

      return () => {
        window.removeEventListener('popstate', handleRouteChange);
      };
    }
  }, []);

  const renderScreen = () => {
    switch (currentScreen) {
      case 'Magician':
        return <MagicianScreen />;
      case 'Genre':
        return <GenreScreen route={{ params: screenParams }} />;
      case 'MovieDetail':
        return <MovieDetailScreen route={{ params: screenParams }} />;
      default:
        return <HomeScreen />;
    }
  };

  return (
    <SafeAreaProvider>
      {renderScreen()}
      <StatusBar style="light" />
    </SafeAreaProvider>
  );
}
