import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { Platform } from 'react-native';

import HomeScreen from './src/screens/HomeScreen';
import GenreScreen from './src/screens/GenreScreen';
import MovieDetailScreen from './src/screens/MovieDetailScreen';
import MagicianScreen from './src/screens/MagicianScreen';

export default function App() {
  // Get current screen directly from URL path
  const getCurrentScreen = () => {
    if (Platform.OS === 'web') {
      const path = window.location.pathname;
      console.log('Current path:', path);

      if (path === '/Magician') {
        return 'Magician';
      } else if (path === '/Genre') {
        return 'Genre';
      } else if (path === '/MovieDetail') {
        return 'MovieDetail';
      }
    }
    return 'Home';
  };

  const currentScreen = getCurrentScreen();

  const renderScreen = () => {
    switch (currentScreen) {
      case 'Magician':
        return <MagicianScreen />;
      case 'Genre':
        return <GenreScreen route={{ params: {} }} />;
      case 'MovieDetail':
        return <MovieDetailScreen route={{ params: {} }} />;
      default:
        return <HomeScreen />;
    }
  };

  return (
    <SafeAreaProvider>
      {renderScreen()}
      <StatusBar style="light" />
    </SafeAreaProvider>
  );
}
