import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Image,
  ActivityIndicator,
  Dimensions,
  FlatList,
  TextInput,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { getGenres, getMoviesByGenre, searchMovies, getTopRatedMovies } from '../services/tmdbService';
import { socketService } from '../services/socketService';
import MovieModal from '../components/MovieModal';

const { width } = Dimensions.get('window');
const POSTER_WIDTH = 156; // 30% larger (120 * 1.3)
const POSTER_HEIGHT = 234; // 30% larger (180 * 1.3)

export default function HomeScreen({ navigation }) {
  const [genres, setGenres] = useState([]);
  const [moviesByGenre, setMoviesByGenre] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [featuredMovies, setFeaturedMovies] = useState([]);

  useEffect(() => {
    loadGenresAndMovies();
    setupSocketConnection();

    return () => {
      socketService.disconnect();
    };
  }, []);

  const setupSocketConnection = () => {
    socketService.connect();
    socketService.onMovieSelected((movie) => {
      console.log('Movie selected by customer:', movie);
    });
  };

  const loadGenresAndMovies = async () => {
    try {
      setLoading(true);

      // Get top 5 highest-rated recent movies for featured section
      try {
        const topMovies = await getTopRatedMovies(1, 20); // Get more to filter by recent
        const recentTopMovies = topMovies
          .filter(movie => new Date(movie.release_date) >= new Date('2020-01-01')) // Recent movies (2020+)
          .sort((a, b) => b.vote_average - a.vote_average) // Sort by rating
          .slice(0, 5); // Take top 5
        setFeaturedMovies(recentTopMovies);
      } catch (error) {
        console.error('Error loading featured movies:', error);
        setFeaturedMovies([]);
      }

      // Get all genres
      const genresData = await getGenres();
      setGenres(genresData);

      // Load top 50 movies for each genre
      const moviesData = {};
      for (const genre of genresData) {
        try {
          const movies = await getMoviesByGenre(genre.id, 1, 50); // Limit to 50 movies
          moviesData[genre.id] = movies;
        } catch (error) {
          console.error(`Error loading movies for genre ${genre.name}:`, error);
          moviesData[genre.id] = [];
        }
      }

      setMoviesByGenre(moviesData);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleMoviePress = (movie) => {
    setSelectedMovie(movie);
    setModalVisible(true);

    // Send movie selection to magician app via socket
    socketService.selectMovie(movie);
  };

  const handleSearch = async (query) => {
    setSearchQuery(query);

    if (query.trim().length === 0) {
      setSearchResults([]);
      setIsSearching(false);
      return;
    }

    if (query.trim().length < 2) return; // Don't search for very short queries

    setIsSearching(true);
    try {
      const results = await searchMovies(query, 1);
      setSearchResults(results);
    } catch (error) {
      console.error('Error searching movies:', error);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const renderMoviePoster = ({ item }) => (
    <TouchableOpacity
      style={styles.moviePoster}
      onPress={() => handleMoviePress(item)}
      activeOpacity={0.7}
    >
      <Image
        source={{
          uri: `https://image.tmdb.org/t/p/w500${item.poster_path}`,
        }}
        style={styles.posterImage}
        resizeMode="cover"
      />

      {/* Rating Badge */}
      {item.vote_average && (
        <View style={styles.ratingBadge}>
          <Text style={styles.ratingText}>
            ★ {item.vote_average.toFixed(1)}
          </Text>
        </View>
      )}

      {/* Movie Info Overlay */}
      <View style={styles.posterOverlay}>
        <Text style={styles.movieTitle} numberOfLines={2}>
          {item.title}
        </Text>
        <View style={styles.movieMeta}>
          <Text style={styles.movieYear}>
            {new Date(item.release_date).getFullYear()}
          </Text>
          {item.vote_average && (
            <Text style={styles.movieRating}>
              ★ {item.vote_average.toFixed(1)}
            </Text>
          )}
        </View>
      </View>
    </TouchableOpacity>
  );

  const renderGenreSection = ({ item: genre }) => {
    const movies = moviesByGenre[genre.id] || [];

    if (movies.length === 0) return null;

    return (
      <View style={styles.genreSection}>
        <View style={styles.genreHeader}>
          <Text style={styles.genreTitle}>{genre.name}</Text>
          <Text style={styles.movieCount}>{movies.length} movies</Text>
        </View>

        <FlatList
          data={movies}
          renderItem={renderMoviePoster}
          keyExtractor={(movie) => movie.id.toString()}
          horizontal
          showsHorizontalScrollIndicator={false}
          contentContainerStyle={styles.moviesRow}
          snapToInterval={POSTER_WIDTH + 16}
          decelerationRate="fast"
        />
      </View>
    );
  };

  const renderSearchResults = () => {
    if (searchResults.length === 0 && searchQuery.trim().length > 0) {
      return (
        <View style={styles.noResults}>
          <Text style={styles.noResultsText}>No movies found for "{searchQuery}"</Text>
        </View>
      );
    }

    return (
      <View style={styles.searchResultsSection}>
        <View style={styles.genreHeader}>
          <Text style={styles.genreTitle}>Search Results</Text>
          <Text style={styles.movieCount}>{searchResults.length} movies</Text>
        </View>

        <FlatList
          data={searchResults}
          renderItem={renderMoviePoster}
          keyExtractor={(movie) => movie.id.toString()}
          horizontal
          showsHorizontalScrollIndicator={false}
          contentContainerStyle={styles.moviesRow}
          snapToInterval={POSTER_WIDTH + 16}
          decelerationRate="fast"
        />
      </View>
    );
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#007AFF" />
        <Text style={styles.loadingText}>Loading movies...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      {/* Yellow IMDB Header */}
      <View style={styles.imdbHeader}>
        <Text style={styles.imdbTitle}>IMDB</Text>
        <View style={styles.searchContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Search movies..."
            placeholderTextColor="#999"
            value={searchQuery}
            onChangeText={handleSearch}
            autoCapitalize="none"
            autoCorrect={false}
          />
          {isSearching && (
            <ActivityIndicator size="small" color="#000" style={styles.searchSpinner} />
          )}
        </View>
      </View>

      {/* Content - Show search results or genres */}
      {searchQuery.trim().length > 0 ? (
        <FlatList
          data={[1]} // Single item to render search results
          renderItem={() => renderSearchResults()}
          keyExtractor={() => 'search'}
          showsVerticalScrollIndicator={false}
          contentContainerStyle={styles.genresList}
        />
      ) : (
        <FlatList
          data={genres}
          renderItem={renderGenreSection}
          keyExtractor={(item) => item.id.toString()}
          showsVerticalScrollIndicator={false}
          contentContainerStyle={styles.genresList}
          onRefresh={loadGenresAndMovies}
          refreshing={loading}
        />
      )}

      <MovieModal
        visible={modalVisible}
        movie={selectedMovie}
        onClose={() => {
          setModalVisible(false);
          setSelectedMovie(null);
        }}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#0a0a0a',
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#0a0a0a',
  },
  loadingText: {
    color: '#fff',
    marginTop: 16,
    fontSize: 16,
  },
  imdbHeader: {
    backgroundColor: '#F5C518', // IMDB yellow
    paddingTop: 20,
    paddingBottom: 20,
    paddingHorizontal: 20,
    elevation: 4,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  imdbTitle: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#000',
    textAlign: 'center',
    marginBottom: 16,
    letterSpacing: 2,
  },
  searchContainer: {
    position: 'relative',
  },
  searchInput: {
    backgroundColor: '#fff',
    borderRadius: 25,
    paddingHorizontal: 20,
    paddingVertical: 12,
    fontSize: 16,
    color: '#000',
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.2,
    shadowRadius: 2,
  },
  searchSpinner: {
    position: 'absolute',
    right: 15,
    top: 12,
  },
  genresList: {
    paddingBottom: 20,
  },
  genreSection: {
    marginBottom: 30,
  },
  genreHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    marginBottom: 16,
  },
  genreTitle: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#fff',
  },
  movieCount: {
    fontSize: 14,
    color: '#888',
  },
  moviesRow: {
    paddingHorizontal: 20,
  },
  moviePoster: {
    width: POSTER_WIDTH,
    height: POSTER_HEIGHT,
    marginRight: 16,
    borderRadius: 12,
    overflow: 'hidden',
    elevation: 3,
